        var keys;
        if (include_grayscales) {
            keys = Object.keys(this.Names);
        }
        else {
            var grayscale_kws = ["white", "gray", "grey", "black"];
            keys = [];
            for (var key of Object.keys(this.Names)) {
                var include = true;
                for (var kw of grayscale_kws) {
                    if (key.includes(kw)) {
                        include = false;
                        break;
                    }
                }
                if (!include) {
                    continue;
                }
                keys.push(key);
            }
        }
        return keys[Math.floor(keys.length * Math.random())];
